// Main User Management Component
export const UserManagement: React.FC<UserManagementProps> = ({
    initialUsers = [],
    onUserSelect,
    onUserUpdate,
    searchPlaceholder,
    className = ''
}) => {
    const userState = useUsers(initialUsers);
    const [searchTerm, setSearchTerm] = useState('');
    const [sortBy, setSortBy] = useState<'name' | 'email' | 'status'>('name');
    const [filterActive, setFilterActive] = useState<boolean | null>(null);

    const debouncedSearchTerm = useDebounce(searchTerm, 300);
    const [savedState, setSavedState] = useLocalStorage('userManagement', {
        sortBy,
        filterActive
    });

    // Filter and sort users
    const filteredAndSortedUsers = useMemo(() => {
        let filtered = userState.users;

        // Apply search filter
        if (debouncedSearchTerm) {
            const term = debouncedSearchTerm.toLowerCase();
            filtered = filtered.filter(user =>
                user.name.toLowerCase().includes(term) ||
                user.email.toLowerCase().includes(term)
            );
        }

        // Apply active filter
        if (filterActive !== null) {
            filtered = filtered.filter(user => user.isActive === filterActive);
        }

        // Apply sorting
        filtered.sort((a, b) => {
            switch (sortBy) {
                case 'name':
                    return a.name.localeCompare(b.name);
                case 'email':
                    return a.email.localeCompare(b.email);
                case 'status':
                    return a.isActive === b.isActive ? 0 : a.isActive ? -1 : 1;
                default:
                    return 0;
            }
        });

        return filtered;
    }, [userState.users, debouncedSearchTerm, sortBy, filterActive]);

    // Handle user selection
    const handleUserSelect = useCallback((user: User) => {
        userState.selectUser(user);
        onUserSelect?.(user);
    }, [userState.selectUser, onUserSelect]);

    // Handle user updates
    const handleUserUpdate = useCallback((id: number, updates: Partial<User>) => {
        userState.updateUser(id, updates);
        const updatedUser = { ...userState.users.find(u => u.id === id)!, ...updates };
        onUserUpdate?.(updatedUser);
    }, [userState.updateUser, userState.users, onUserUpdate]);

    // Save state changes
    useEffect(() => {
        setSavedState({ sortBy, filterActive });
    }, [sortBy, filterActive, setSavedState]);

    return (
        <UserContext.Provider value={userState}>
            <div className={`user-management ${className}`}>
                <div className="controls">
                    <UserSearch
                        value={searchTerm}
                        onChange={setSearchTerm}
                        placeholder={searchPlaceholder}
                    />

                    <div className="filters">
                        <select
                            value={sortBy}
                            onChange={(e) => setSortBy(e.target.value as any)}
                        >
                            <option value="name">Sort by Name</option>
                            <option value="email">Sort by Email</option>
                            <option value="status">Sort by Status</option>
                        </select>

                        <select
                            value={filterActive?.toString() || 'all'}
                            onChange={(e) => {
                                const value = e.target.value;
                                setFilterActive(
                                    value === 'all' ? null : value === 'true'
                                );
                            }}
                        >
                            <option value="all">All Users</option>
                            <option value="true">Active Only</option>
                            <option value="false">Inactive Only</option>
                        </select>
                    </div>
                </div>

                <div className="user-list">
                    {filteredAndSortedUsers.length === 0 ? (
                        <div className="empty-state">
                            {debouncedSearchTerm ? 'No users found matching your search.' : 'No users available.'}
                        </div>
                    ) : (
                        filteredAndSortedUsers.map(user => (
                            <UserCard
                                key={user.id}
                                user={user}
                                isSelected={userState.selectedUser?.id === user.id}
                                onSelect={() => handleUserSelect(user)}
                                onUpdate={(updates) => handleUserUpdate(user.id, updates)}
                                onDelete={() => userState.deleteUser(user.id)}
                            />
                        ))
                    )}
                </div>

                {userState.selectedUser && (
                    <div className="user-details">
                        <h3>Selected User: {userState.selectedUser.name}</h3>
                        <p>Email: {userState.selectedUser.email}</p>
                        <p>Status: {userState.selectedUser.isActive ? 'Active' : 'Inactive'}</p>
                    </div>
                )}
            </div>
        </UserContext.Provider>
    );
};

