def _extract_dependencies(self, code: str) -> List[str]:
        """Extract Python dependencies from code"""
        import re

        dependencies = []

        # Extract standard imports
        import_patterns = [
            r'import\s+([a-zA-Z_][a-zA-Z0-9_]*)',
            r'from\s+([a-zA-Z_][a-zA-Z0-9_]*)\s+import',
        ]

        for pattern in import_patterns:
            matches = re.findall(pattern, code)
            for match in matches:
                if '.' in match:
                    # Get root package
                    root_package = match.split('.')[0]
                    dependencies.append(root_package)
                else:
                    dependencies.append(match)

        # Filter out standard library modules
        stdlib_modules = {
            'os', 'sys', 'json', 'time', 'datetime', 'logging', 'typing',
            'dataclasses', 'pathlib', 'functools', 'itertools', 'collections',
            'concurrent', 'asyncio', 'unittest', 'tempfile', 're'
        }

        external_deps = [dep for dep in dependencies if dep not in stdlib_modules]

        # Add common framework dependencies based on code content
        if 'fastapi' in code.lower() or 'FastAPI' in code:
            external_deps.extend(['fastapi', 'uvicorn', 'pydantic'])
        if 'django' in code.lower():
            external_deps.append('django')
        if 'sklearn' in code or 'scikit-learn' in code:
            external_deps.extend(['scikit-learn', 'numpy', 'pandas'])
        if 'tensorflow' in code.lower():
            external_deps.append('tensorflow')
        if 'torch' in code.lower() or 'pytorch' in code.lower():
            external_deps.append('torch')
        if 'requests' in code:
            external_deps.append('requests')
        if 'aioredis' in code:
            external_deps.append('aioredis')
        if 'asyncpg' in code:
            external_deps.append('asyncpg')

        return list(set(external_deps))
